export default function determineType(beer, typeOfLike) {
  let numberOfLikes
  if (typeOfLike === 'increase') {
    numberOfLikes = beer.likes + 1
  } else if (typeOfLike === 'decrease') {
    numberOfLikes = beer.likes - 1
  }
  return numberOfLikes
}
