export default function updateLikesCount(state, action) {
  let newState = state.map(beer => {
    if (beer.id === action.payload.id) {
      return Object.assign({}, beer, { likes: action.payload.likes })
    } else {
      return beer
    }
  })
  console.log(newState)
  return [...newState]
}
