export function updateLikesCount(state, action) {
  let newState = state.map(beer => {
    if (beer.name === action.payload.name) {
      return Object.assign({}, action.payload, { likes: action.payload.likes })
    } else {
      return beer
    }
  })
  return [...newState]
}

export function determineType(beer, typeOfLike) {
  if (typeOfLike === 'increase') {
    numberOfLikes = beer.likes + 1
  } else if (typeOfLike === 'decrease') {
    numberOfLikes = beer.likes - 1
  }
  return numberOfLikes
}
