import React from 'react'
import { connect } from 'react-redux'
import * as actions from 'actions'
import Beer from 'components/Beer'
import BeerInput from 'components/BeerInput'
import axios from 'axios'
import { localProxy, fsBeer } from 'endpoints'

class Cooler extends React.Component {
  state = {
    displayAddBeerInput: false,
    updatedBeers: [],
    beerWithId: ''
  }

  componentDidMount() {
    this.props.openCooler()
  }

  renderBeers() {
    return this.props.beers.map(beer => {
      return <Beer beer={beer}
        increaseLikes={this.increaseLikes}
        decreaseLikes={this.decreaseLikes}
      />
    })
  }

  fetchBeersAndMatchId(beer) {
    const response = axios.get(localProxy + fsBeer)
    .then(response => {
      this.setState({updatedBeers: response.data})
      this.state.updatedBeers.forEach(b => {
        if (beer.name === b.name) {
          this.setState({beerWithId: b})
        }
      })
    })
    .catch(error => {
      return new Error(error)
    })
  }

  verifyId = (beer) => {
    let newBeer
    if (!beer.id) {
      this.setState({doneUpdating: false})
      return new Promise((resolve, reject) => {
        this.fetchBeersAndMatchId(beer)
        setTimeout(() => {
          resolve();
        }, 6000);
      }).then(() => {
      });
    } else {
      this.setState({beerWithId: beer})
    }
  }

  increaseLikes = (beer) => {
    this.verifyId(beer)
    this.props.updateLikes(this.state.beerWithId, 'increase')
  }

  decreaseLikes = (beer) => {
    this.props.updateLikes(beer, 'decrease')
  }

  toggleBeerInput = () => {
    this.setState({displayAddBeerInput: !this.state.displayAddBeerInput})
  }

  render() {
    return (
      <div>
        {!this.state.displayAddBeerInput &&
        <button
          className="add-beer"
          onClick={this.toggleBeerInput}
          >
          Click here to add your favorite beer!
        </button>}
        <div>
          {this.state.displayAddBeerInput && <BeerInput openCooler={this.props.openCooler} />}
        </div>
        <button
          className='done-adding-beer'
          onClick={this.toggleBeerInput}
          hidden={!this.state.displayAddBeerInput}>
          I'm done adding beers
        </button>
        <h1>Cooler Beers!</h1>
        <ul>
          {this.renderBeers()}
        </ul>
      </div>
    )
  }
}

function mapStateToProps(state) {
  return { beers: state.beers }
}

export default connect(mapStateToProps, actions)(Cooler)
